import { createContext, useEffect, useState } from "react";
import est from '../assets/estrela.mp4'
import oceano from '../assets/oceano.mp4'

export const AuthContext = createContext({});

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState();

  const [background, setBackground] = useState(est)
const handlechange = (link) => {
  if(link === 'oceano'){
    setBackground(oceano)    
  }
}
  useEffect(() => {
    const userToken = localStorage.getItem("user_token");
    const usersSorage = localStorage.getItem("users_db");
    if (userToken && usersSorage) {
      const hasUser = JSON.parse(usersSorage)?.filter(
        (user) => user.email === JSON.parse(userToken).email
      );
      if (hasUser) setUser(hasUser[0]);
    }
  }, []);

  const signin = (email, password) => {
    const usersStorage = JSON.parse(localStorage.getItem("users_db"));
    const hasUser = usersStorage?.filter((user) => user.email === email);

    if (hasUser?.length) {
      if (hasUser[0].email === email && hasUser[0].password === password) {
        const token = Math.random().toString(36).substring(2);
        localStorage.setItem("user_token", JSON.stringify({ email, token }));
        setUser({ email, password });
        return;
      } else {
        return "E-mail ou senha incorretos";
      }
    } else {
      return "Usuário não cadastrado";
    }
  };

  const signup = (email, password) => {
    const usersStorage = JSON.parse(localStorage.getItem("users_db"));
    const hasUser = usersStorage?.filter((user) => user.email === email);

    if (hasUser?.length) {
      return "Já tem uma conta com esse E-mail";
    }
    let newUser;

    if (usersStorage) {
      newUser = [...usersStorage, { email, password }];
    } else {
      newUser = [{ email, password }];
    }
    localStorage.setItem("users_db", JSON.stringify(newUser));
    return;
  };

  const signout = () => {
    setUser(null);
    localStorage.removeItem("user_token");
  };

  return (
    <AuthContext.Provider
      value={{
        user,
        signed: !!user,
        signin,
        signup,
        signout,
        background,
        setBackground, 
        handlechange
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};
